import express from 'express';
import { repositoryRoute } from './routes';
import cors from 'cors';

class App {
	public app: express.Express;
	constructor() {
		this.app = express();

		this.config();

		this.app.get('/', (req, res) => res.json({ ok: true }));
	}

	private config(): void {
		const accessControl: express.RequestHandler = (_req, res, next) => {
			res.header('Access-Control-Allow-Origin', '*');
			res.header(
				'Access-Control-Allow-Methods',
				'GET,POST,DELETE,OPTIONS,PUT,PATCH'
			);
			res.header('Access-Control-Allow-Headers', '*');
			next();
		};
		this.app.use(cors());
		this.app.use(express.json());
		this.app.use(accessControl);
		this.app.use('/repositories', repositoryRoute);
	}

	public start(PORT: string | number): void {
		this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
	}
}

export { App };

import express from 'express';
import repositoryController from '../controllers/respository.controller';

const router = express.Router();







router.get('/:language', repositoryController.listRepositoriesByLanguage);
router.get('/', repositoryController.listAllRepositories);

export default router;

import { Request, Response } from 'express';
import { GithubAPIService} from '../services';

export default class RepositoryController {
	public static listRepositoriesByLanguage = async (req: Request, res: Response) => {
		try {
			const { language } = req.params;
			const data = await GithubAPIService.listRepositoryByLanguage(language as string);            
			return res.status(200).json(data);
		} catch (error) {
			return res.status(500).json(error.message);
		}
	};
	public static listAllRepositories = async (req: Request, res: Response) => {
		try {
			const data = await GithubAPIService.listRepositories();          
			return res.status(200).json(data);
		} catch (error) {
			return res.status(500).json(error.message);
		}
	};
}

import Repository from '../../database/models/repository.model';
import { TRepositoryUtils } from '../../types/types';
import { Op } from 'sequelize';
import _ from 'lodash';

class GithubAPIService {
	static saveRepositories = async (repositories: TRepositoryUtils[]): Promise<void> => {
		try {
			await Repository.sync();
			await Repository.bulkCreate(repositories);
			console.log('Repositories saved successfully');
		} catch (error) {
			return error.message;
		}
	};

	static listRepositories = async (): Promise<Record<string, TRepositoryUtils[]>> => {
		try {			
			const repositories = await Repository.findAll({
				where: {
					language: {[Op.ne]: null},
				},
			});

			const groupedRepos= _.groupBy(repositories, 'language');	

			return groupedRepos;

		} catch (error) {
			return error.message;
		}
	};

	static listRepositoryByLanguage = async (language: string): Promise<TRepositoryUtils[]> => {
		try {
			const repository = await Repository.findAll({ where: { language} });
			return repository;
		} catch (error) {
			return error.message;
		}
	};

	static deletaAllRepository = async () => {
		try {
			await Repository.destroy({ where: {} });
			return 'Repository List clear';
		} catch (error) {
			return error.message;
		}
	};
}

export default GithubAPIService;

import { DataTypes, Model } from 'sequelize';
import db from './index';

class Repository extends Model {
	public id!: number;
	public owner: string;
	public ownerRepo: string;
	public ownerAvatar: string; 
	public repoName: string;
	public  description: string; 
	public  stars: number; 
	public  forks: number; 
	public  language: string; 
	public  repoLink: string; 
}

Repository.init({
	id: {
		allowNull: false,
		autoIncrement: true,
		primaryKey: true,
		type: DataTypes.INTEGER,
	},
	owner: {
		type: DataTypes.STRING,
	},
	ownerRepo: {
		type: DataTypes.STRING,
		allowNull: true,
	},
	ownerAvatar: {
		type: DataTypes.STRING,
		allowNull: false,
	},
	repoName: {
		type: DataTypes.STRING,
		allowNull: false,
	},
	description: {
		type: DataTypes.TEXT,
		allowNull: true,
	},
	stars: {
		type: DataTypes.INTEGER,
		allowNull: false,
	},
	forks: {
		type: DataTypes.INTEGER,
		allowNull: false,
	},
	language: {
		type: DataTypes.STRING,
		allowNull: false,
	},
	repoLink: {
		type: DataTypes.STRING,
		allowNull: false,
	},
}, {
	sequelize: db,
	createdAt: true,
	updatedAt: true,
	modelName: 'repository',
	tableName: 'repositories',
});

export default Repository;

import { Sequelize } from 'sequelize';
import * as config from '../config/database';

export default new Sequelize(config);

import axios from 'axios';
import { TRepositoryResponse } from '../../../types/types';
import 'dotenv/config';

class FetchRepositoriesData {
	static fetchData = async (repoList): Promise<TRepositoryResponse[]> => {	
		const config = {
			headers: { Authorization: `Bearer ${process.env.GITHUB_TOKEN}` }
		};
		const repositories: TRepositoryResponse[] = repoList.map(async ({owner, repo}) => {
			const response = await axios.get(`https://api.github.com/repos/${owner}/${repo}`, config);
			return response.data;
		});
		const data = await Promise.all(repositories);

		return data;
	};
}
export default FetchRepositoriesData;

import repositoriesName from './repositoriesName';
import { TRepositoryUtils, TRepositoryResponse } from '../../../types/types';
import FetchRepositoriesData from './fetchRepositoryFetchRepositoriesData';
import 'dotenv/config';

class GenerateTrendingList {
	static generateList = async (language: string): Promise<TRepositoryUtils[]> => {
		const repoList = await repositoriesName.getRepositoryAndUserNames(`https://github.com/trending/${language}?since=daily`);

		const fetchTrendingRepositories: TRepositoryResponse[] = await FetchRepositoriesData.fetchData(repoList);   

		const repositories: TRepositoryUtils[] = fetchTrendingRepositories.map((item: TRepositoryResponse) => ({
			owner: item.owner.login,
			ownerRepo: item.owner.html_url,
			ownerAvatar: item.owner.avatar_url,
			repoName: item.name,
			description: item.description,
			stars: item.stargazers_count,
			forks: item.forks_count,
			language: item.language,
			repoLink: item.html_url
		}));

		return repositories;
	};
}

export default GenerateTrendingList;

import * as cheerio from 'cheerio';
import axios from 'axios';

class ScrapTrendingRepositories {
	static  getRepositoryAndUserNames = async (url) => {
		try {
			const response = await axios.get(url);
			const html = response.data;
			const $ = cheerio.load(html);
			const repoList = $('h1 > a');
			const repoNames = repoList.toArray().map((element) => {
				const repo = $(element).text().trim().split('/').pop().trim();
				const owner = $(element).text().trim().split('/')[0].trim();
				return {
					owner,
					repo,
				};
			});    
			return repoNames;
		} catch (error) {
			console.error(error);
		}
	};
}
export default ScrapTrendingRepositories;
